#include "test_runner.h"
#include "profile.h"
using namespace std;

class Solution {
public:
    int longestValidParentheses(string s) {
		//Runtime: 8 ms, faster than 87.51% of C++ online submissions for Longest Valid Parentheses.
		//Memory Usage: 6.7 MB, less than 99.69% of C++ online submissions for Longest Valid Parentheses.
		int maximum = 0, left = 0, right = 0;
		for (int i=0; i<s.size(); i++){//Прямой проход
			if (s[i]=='('){
				left++;
			} else {
				right++;
				if (left == right){
					maximum = max(maximum, right*2);
					//cout<<"-> i="<<i<<" maximum="<<right*2<<"?"<<endl;
				} else if (right>left){
					left = right = 0;
				}
			}
		}
		left = right = 0;
		for (int i=0; i<s.size(); i++){//Обратный проход
			if (s[s.size()-1-i]=='('){
				left++;
				if (left == right){
					maximum = max(maximum, right*2);
					//cout<<"<- i="<<i<<" maximum="<<right*2<<"?"<<endl;
				} else if (right<left){
					left = right = 0;
				}
			} else {
				right++;
			}
		}
		
		/*
		//Runtime: 1436 ms, faster than 5.06% of C++ online submissions for Longest Valid Parentheses.
		//Memory Usage: 6.7 MB, less than 99.85% of C++ online submissions for Longest Valid Parentheses.
		string_view s(ss);//673ms! А это всего лишь замена string на string_view o_O
		//820ms
		if (s.empty()) return 0;
		int maximum = 0;
        for (int i=0; i<s.size()-1; i++){
			if (s[i]=='('){
				int balance = 1;
				int i_next = i;
				for (int j=i+1; j<s.size(); j++){
					if (s[j]=='('){
						balance++;
					} else {
						balance--;
						if (balance==0){
							maximum = max(maximum, j-i+1);
							i_next = j;
						} else if (balance<0){
							i_next=j-1;
							break;
						}
					}
				}
				i = i_next;
			}
		}
        */
		
		//cout<<"Answer="<<maximum<<endl;
        return maximum;
    }
};

